<script>
export default {
  name: 'TitleScreen',
  props: {
    score: {
      type: Object,
      required: true,
    },
    isPaused: {
      type: Boolean,
      required: true,
    },
    isStarted: {
      type: Boolean,
      required: true,
    },
    isGameOver: {
      type: Boolean,
      required: true,
    },
  },
  methods: {
    handleButtonClick() {
      if (this.isGameOver) {
        this.$emit('startOver')
      } else {
        this.$emit('start')
      }
    },
  },
  computed: {
    playButtonText() {
      return this.isGameOver
        ? 'Play again'
        : 'Play'
    },
  },
}
</script>
